mapper.select:
SELECT * from salesman_info
deleted = 0 and identity <> 2
-- 销售组长，可以查看自己手下销售的信息 AND belong = #{belong}
ORDER BY create_time DESC limit #{start}, #{pageSize}

SELECT COUNT(*) from salesman_info
deleted = 0 and identity <> 2
-- 销售组长，可以查看自己的信息和自己手下销售的信息 AND belong = #{belong}

SELECT * from salesman_info
  WHERE deleted = 0
    and username = #{username}
or name = #{name}
or phone = #{phone}
limit 0,1

SELECT * from customer_info
WHERE deleted = 0
and phone = #{phone}

SELECT * FROM ( SELECT a.`id` AS id, a.`salesmanid` AS salesmanId, c.salesmanName AS salesmanName, c.salesmanLeaderName as salesmanLeaderName, a.`receptiontime` AS receptionTime, a.`company` AS company, a.`name` AS NAME, a.`phone` AS phone, a.`sex` AS sex, a.`agerange` AS ageRange, a.`family_structure` AS familyStructure, a.`residential_region` AS residentialRegion, a.`residential_district` AS residentialDistrict, a.`workingarea` AS workingArea, a.`msgaccess` AS msgAccess, a.`company_type` AS companyType, a.`job` AS job, a.`preferred_product` AS preferredProduct, a.`preferred_area` AS preferredArea, a.`psychological_unit_price` AS psychologicalUnitPrice, a.`psychological_total_price` AS psychologicalTotalPrice, a.`reason` AS reason, a.`payway` AS payWay, a.`trip_tools` AS tripTools, a.`key_factor` AS keyFactor, a.`asking_time` AS askingTime, a.`loan_time` AS loanTime, a.`willingness` AS willingness, a.`desc` AS 'desc' FROM customer_info a RIGHT JOIN ( SELECT b.id, b. NAME AS salesmanName, b.salesmanLeaderName as salesmanLeaderName FROM ( SELECT aa.*, dd. NAME AS salesmanLeaderName FROM salesman_info aa LEFT JOIN ( SELECT bb.* FROM salesman_info bb ) AS dd ON aa.belong = dd.id ) b WHERE b.deleted = 0
AND ( b.id = #{salesmanId} OR b.belong = #{salesmanId} )
) AS c ON a.salesmanid = c.id AND a.deleted = 0
and a.name like ${name}
) d WHERE d.id IS NOT NULL limit #{start}, #{pageSize}

SELECT COUNT(*)
from customer_track_info a
WHERE a.deleted = 0 and a.customer_info_id = #{customerInfoId}

